import React, { useEffect, useState } from 'react';
import personService from './services/persons';
import Person from './components/Person.js';
import { getNextKeyDef } from '@testing-library/user-event/dist/keyboard/getNextKeyDef';

const Filter = (props) => {
  return (
    <form>
      <div>
        filter shown with <input 
          value={props.filterName}
          onChange={props.handleFilterName}
        />
      </div>
    </form>
  )
}

const PersonForm = (props) => {
  return (
    <form onSubmit={props.addPerson}>
        <div>
          name: <input 
            value={props.newName}
            onChange={props.handleNameChange}
          />
        </div>
        <div>
          number: <input 
            value={props.newNum}
            onChange={props.handleNumChange}
          />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
  )
}

const Notification = ({message}) => {
  if (message === null) {
    return null
  }
  return (
    <div className="error">
      {message}
    </div>
  )
}

const App = () => {
  const [persons, setPersons] = useState([]); 
  const [newName, setNewName] = useState('');
  const [newNum, setNewNum] = useState('');
  const [filterName, setFilterName] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    personService
      .getAll()
      .then(response => {
        setPersons(response);
      });
  }, []);

  const addPerson = (event) => {
    event.preventDefault();
    const personObject = {
      name: newName,
      number: newNum,
    };

    const name = persons.filter(person => person.name === newName);

    if (newName.length !== 0) {
      if (name.length !== 0) {
        if (name[0].name === newName) {
          personService
            .update(name[0].id, personObject)
            .then(error => {
              setMessage(
                `Changed ${name[0].name} number`
              )
              setTimeout(() => {
                setMessage(null)
                window.location.reload()
              }, 3000)
              setNewName('');
              setNewNum('');
              setFilterName('');
            })
        }
      } else {          
        personService
          .create(personObject)
          .then(response => {
            setPersons(persons.concat(response))
            setMessage(
              `Added ${newName}`
            )
            setTimeout(() => {
              setMessage(null)
              window.location.reload()
            }, 3000)
            setNewName('');
            setNewNum('');
            setFilterName('');
          })
          .catch(error => {
            console.log(error._message)
            setMessage(error.name, error.message)
          })

      } 
    }
  }

  const handleNameDel = (event) => {
    const id = event.target.id;
    const nameObject = persons.find(n => n.id === id)
    const name = nameObject.name

    personService
        .del(id)
        .then(error => {
          setMessage(
            `Deleted ${name}`
          )
          setTimeout(() => {
            setMessage(null)
            window.location.reload()
          }, 3000);
        })
  }

  const handleNameChange = (event) => {
    setNewName(event.target.value);
  }

  const handleNumChange = (event) => {
    setNewNum(event.target.value);
  }

  const handleFilterName = (event) => {
    setFilterName(event.target.value);
  }

  return (
    <div>
      <h1>Phonebook</h1>

        <Notification message={message} />

        <Filter filterName={filterName} handleFilterName={handleFilterName} />

      <h2>Add a new</h2>

        <PersonForm  addPerson={addPerson} 
          newName={newName} handleNameChange={handleNameChange} 
          newNum={newNum} handleNumChange={handleNumChange} 
        />

      <h2>Numbers</h2>

      <div>
        {persons.map(person =>
          <Person 
            key={person.id} 
            person={person} 
            handleNameDel={handleNameDel} 
          />
        )}

      </div>
    </div>
  );

};

export default App;
